class MenuButton extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isCardView: false,
        }
    }

    render() {
        return (
            <a className="btn btn-primary" onClick={()=>this.setState({ isCardView: !this.state.isCardView })}  onMouseDown={this.props.handleMouseDown}>
                { this.state.isCardView
                    ? <span className="glyphicon glyphicon-remove-sign"><FaBars/></span>
                    : <span className="glyphicon glyphicon-ok-sign" >-</span>
                }
                &nbsp;&nbsp;BUTTON
            </a>
        );
    }

}
export default MenuButton;
