        this.mouseEnter = this.handleMouseEnter().bind(this);
        this.mouseLeave = this.handleMouseLeave().bind(this);
        this.state={
            visibility_my:false
        };
    }


    handleMouseEnter  = () => {this.setState({visibility_my:!this.state.visibility_my});
    console.log(this.state.visibility_my)
    };
    handleMouseLeave = () => {this.setState({visibility_my:!this.state.visibility_my})};
    render() {
        let visibility = "hide";

        if(this.state.visibility_my){
            visibility = "show";
        }
        return(
                <div>
                        <div className="container" >
                            <img src={this.props.image} alt={"kek"} className="image"/>
                            <div className="overlay" >
                                <a href={this.props.my_key} className="icon" title="instagram">
                                    <i className={'fa fa-instagram ' + visibility}/>
                                </a>
                            </div>
                        </div>
                </div>
        )}
}
export default Template;
