import React, { useState } from 'react';
import {useDispatch, useSelector} from "react-redux";
import {FaBars,FaShoppingBag,FaTimes} from 'react-icons/fa';
import BasketIco from "./BasketIco";
import "./styles.css"
const CartList = (props) =>{
    const dispatch = useDispatch();
    const basket_arr = useSelector(state => state.basket_arr);
    let new_arr = basket_arr.map( (x,index) => {
        return(


            <div key={index} className="row" style={{backgroundColor: index%2===0 ? "#16C44A" : "#007F26"}}>
                <div className="col-4"><img src={x.img} alt={"kek"} className={'image grow '}/>
                </div>
                <div className="col-4"> {x.price}</div>
                <div className="col-3"> {x.text}</div>
                <div className="col-1"><button className="btn"><FaTimes/></button></div>

            </div>

                )
    });
    return (
        <div className="main">
            <div className="container">
                <div className="row">
                    <div className="col-12 align-self-center">Bag<BasketIco/></div>
                    <hr/>
                    {/*<div className="col-6 align-self-center"><BasketIco/></div>/*/}
                </div>
                {new_arr}
            </div>
        </div>

    );
};

export default CartList;
