import React,{Component} from "react";

import "./Menu.css"
import MenuButton from "./MenuButton";
import {connect} from "react-redux";
class Menu extends Component{

    dress = () => {
        this.props.dispatch({ type: 'DRESS' });
    };
    render() {
        let visibility = "hide";

        if(this.props.menuVisibility){
            visibility = "show";
        }

        return (
            <div id="flyoutMenu" onMouseDown={this.props.handleMouseDown} className={visibility}>
                <MenuButton/>
                <button type="button" className="btn btn-primary" onClick={this.dress}>+</button>
                <h2>Bags</h2>
                <h2>Dress</h2>
                <h2>Pants</h2>
                <h2>Skirt</h2>
                <h2>Jacket</h2>

            </div>
        )
        }
}


function mapStateToProps() {
    return( {
        count: state.count,
        display_arr: state.display_arr
    });
}

export default connect(mapStateToProps)(Menu);
