import '../../App.css';
import React, { Component } from "react";
import MenuButton from "./MenuButton";
import Menu from "./Menu"
import Template from "../elements/Template";


class MenuCont extends Component {


    constructor(props) {
    super(props);

    this.state = {
        visible : false,
        medias : {}
    };
    this.handleMouseDown = this.handleMouseDown.bind(this);
    this.toggleMenu = this.toggleMenu.bind(this);
  }
  toggleMenu(){
        this.setState({
            visible:!this.state.visible
        })
  }
  handleMouseDown(e){
        this.toggleMenu();
        console.log("clicked");
        let ig = require('instagram-scraping');

        ig.scrapeUserPage('nefritistore').then(result => {

          // let {media} = result.medias;
            this.setState({...this.state, dani: result.total});
          console.dir(result.medias);
         });

        e.stopPropagation();
  }


  render() {
    return (
        <div>
            <Template count={this.state.dani}/>
            <Menu handleMouseDown={this.handleMouseDown} menuVisibility={this.state.visible}/>
            <MenuButton handleMouseDown={this.handleMouseDown}/>

        </div>

  );
  }
}



export default MenuCont;
