import arr from "./output";
import React, { useState } from 'react';
import {useDispatch, useSelector} from "react-redux";
import Template from "./Template";
import {Container} from "@inlet/react-pixi";
// let my_arr=[];
// for (let i = 0; i <3; i++) {
//     my_arr+=arr.pop();
// }
const News = (props) =>{

    function changeBackground(e) {
        e.target.style.opacity = "10%";
    }
    const full = arr.map((x,index)=>
    {
        return (
            <td key={index}>
                <Template image={x.img_url} text={x.caption} my_key={x.key} key={index} onMouseOver={changeBackground()} />
            </td>
        );
    });
    let new_arr =[];
    for (let i = 0; i <3; i++) {
        new_arr.push(full.pop());
    };
  return(
      <div>
          <table>
              <tbody>
              {new_arr}
              </tbody>
          </table>
      </div>
  )
};
export default News;
