PIXI.settings.SCALE_MODE = PIXI.SCALE_MODES.NEAREST

const bunny = 'https://s3-us-west-2.amazonaws.com/s.cdpn.io/693612/IaUrttj.png'
const { Stage, Sprite } = ReactPixi
let scale = { x: 1, y: 1 }

const App = () => (
  <Stage width={500} height={500} options={{ backgroundColor: 0x012b30 }}>
    <Sprite
      x={250}
      y={250}
      anchor={[0.5, 0.5]}
      interactive={true}
      scale={scale}
      image={bunny}
      pointerdown={() => {
        console.log('click')
        scale.x *= 1.25
        scale.y *= 1.25

        render()
      }}
    />
  </Stage>
);

function render() {
  ReactDOM.render(<App />, document.body)
}

render()
